    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        loginController lc = new loginController();
        usuario user =new usuario();
        String txtUsuarioGet = txtUsuario.getText();
        String txtContrasenaGet = txtContrasena.getText();
        
        user = lc.login(txtUsuarioGet, txtContrasenaGet);
            
        if(txtUsuarioGet.equals("") || txtContrasenaGet.equals("")){
            JOptionPane.showMessageDialog(null , "No se ha insertado los datos necesarios","ERROR", JOptionPane.WARNING_MESSAGE);
        }else{
            if(user.getNombre() != null && user.getContrasena() != null){
                menuPrincipal mnuPrincipal = new menuPrincipal();
                mnuPrincipal.setDatos(user.getNombre(), user.getIdRol());
                mnuPrincipal.setVisible(true);
                this.setVisible(false);
                mnuPrincipal.setLocationRelativeTo(null);
            }
            else{
                JOptionPane.showMessageDialog(null, "Error ingresar los datos", "ERROR", JOptionPane.ERROR_MESSAGE);
            }
        }
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

